'use strict';

const configure = require('../lib/configure');

const _require = require('../lib/querystring'),
      objectToQuery = _require.objectToQuery;

const _require2 = require('../lib/fetch'),
      ok = _require2.ok;

const toCamel = require('../lib/to-camel');

module.exports = configure(({
  fetch,
  apiUrl,
  apiPath,
  headers
}) => {
  return async (cid, options) => {
    options = options || {};
    const qs = objectToQuery({
      arg: cid.toString(),
      ...(options.qs || {})
    });
    const url = "".concat(apiUrl).concat(apiPath, "/block/stat").concat(qs);
    const res = await ok(fetch(url, {
      signal: options.signal,
      headers: options.headers || headers
    }));
    return toCamel((await res.json()));
  };
});
