const _require = require('../lib/querystring'),
      objectToQuery = _require.objectToQuery;

const configure = require('../lib/configure');

const _require2 = require('../lib/fetch'),
      ok = _require2.ok;

module.exports = configure(({
  fetch,
  apiUrl,
  apiPath,
  headers
}) => {
  return async (peerId, options) => {
    if (!options && typeof peerId === 'object') {
      options = peerId;
      peerId = null;
    }

    options = options || {};
    const qs = objectToQuery({
      peer: peerId,
      ...(options.qs || {})
    });
    const url = "".concat(apiUrl).concat(apiPath, "/bitswap/wantlist").concat(qs);
    const res = await ok(fetch(url, {
      signal: options.signal,
      headers: options.headers || headers
    }));
    const data = await res.json();
    return (data.Keys || []).map(item => item['/']);
  };
});
