'use strict';

const configure = require('../lib/configure');

const _require = require('../lib/fetch'),
      ok = _require.ok;

const _require2 = require('../lib/querystring'),
      objectToQuery = _require2.objectToQuery;

const toCamel = require('../lib/to-camel');

module.exports = configure(({
  fetch,
  apiUrl,
  apiPath,
  headers
}) => {
  return async options => {
    options = options || {};
    const qs = objectToQuery(options.qs);
    const url = "".concat(apiUrl).concat(apiPath, "/bitswap/stat").concat(qs);
    const res = await ok(fetch(url, {
      signal: options.signal,
      headers: options.headers || headers
    }));
    const data = toCamel((await res.json()));
    data.wantlist = (data.wantlist || []).map(item => item['/']);
    return data;
  };
});
