'use strict';

const _require = require('../lib/querystring'),
      objectToQuery = _require.objectToQuery;

const configure = require('../lib/configure');

const _require2 = require('../lib/fetch'),
      ok = _require2.ok;

const toCamel = require('../lib/to-camel');

module.exports = configure(({
  fetch,
  apiUrl,
  apiPath,
  headers
}) => {
  return async options => {
    options = options || {};
    const qs = objectToQuery({
      verbose: options.verbose,
      streams: options.streams,
      latency: options.latency,
      ...(options.qs || {})
    });
    const url = "".concat(apiUrl).concat(apiPath, "/swarm/peers").concat(qs);
    const res = await ok(fetch(url, {
      signal: options.signal,
      headers: options.headers || headers
    }));
    const data = await res.json();
    return (data.Peers || []).map(p => {
      const peerInfo = toCamel(p);

      if (peerInfo.streams) {
        peerInfo.streams = peerInfo.streams.map(toCamel);
      }

      return peerInfo;
    });
  };
});
